mapper.select:
select m.mid pid, m.mname title, m.url, m.path as href, m.iconCls icon, m.parent_mid from admin_menu m ,admin_rule_menu rm ,admin_info_rule ur ,admin_info u where m.mid = rm.mid and rm.rid = ur.rid and ur.admin_id = #{uid} and m.enabled =2
and m.parent_mid = #{parent_mid}
GROUP BY m.mid

mapper.insert:
insert into admin_menu (mid, mname, url, 
  path, iconCls, enabled, 
  parent_mid, create_date, cteate_id
  )
values (#{mid,jdbcType=INTEGER}, #{mname,jdbcType=VARCHAR}, #{url,jdbcType=VARCHAR}, 
  #{path,jdbcType=VARCHAR}, #{iconcls,jdbcType=VARCHAR}, #{enabled,jdbcType=INTEGER}, 
  #{parentMid,jdbcType=INTEGER}, #{createDate,jdbcType=TIMESTAMP}, #{cteateId,jdbcType=VARCHAR}
  )
